import React from "react";
import "../styles/styleLogin.css";
import "../styles/bootstrap.min.css";
// import logo from "../images/logoM.png";
import { withRouter } from "react-router-dom";
import { connect } from "unistore/react";
import { actions } from "../store";

class LoginPayer extends React.Component {
  render() {
    return (
      <div className="loginWrapper fadeInDown">
        <div id="loginFormContent">
          <div className="fadeIn first">
            <div className="container-fuild loginJudul">
              <span>Masuk ke ePajak</span>
            </div>
            <img
              style={{ marginTop: "30px", marginBottom: "30px", width: "35%" }}
              src="https://image.flaticon.com/icons/png/128/2168/2168759.png"
              id="icon"
              alt="User Icon"
            />
          </div>
          {/* <!-- Login Form --> */}
          <form onSubmit={"#"}>
            <div style={{ display: "flex", marginBottom: "10px" }}>
              <span className="loginPilihRole">Masuk Sebagai : </span>
              <select
                onChange={"#"}
                name="role"
                class="loginRole"
                style={{ height: "34px", borderRadius: "5px" }}
              >
                <option value="Officer" selected>
                  Petugas Pajak
                </option>
                <option value="Surveyor">Surveyor</option>
                <option value="Payer">Wajib Pajak</option>
              </select>
            </div>

            <span>NPWP</span>

            <input
              type="text"
              id="NPWP"
              className="col-md-12 col-sm-12 fadeIn second"
              name="NPWP"
              placeholder="Your Username"
              //   onChange={}
            />

            <input
              type="password"
              id="password"
              className="fadeIn third"
              name="password"
              placeholder="Your Password"
              //   onChange={}
            />
            <input
              type="submit"
              className="fadeIn fourth"
              value="Log In"
              style={{ marginBottom: "15px", marginTop: "10px" }}
              //   onClick={this.handleLogin}
            />
          </form>
        </div>
      </div>
    );
  }
}

export default connect("", actions)(withRouter(LoginPayer));
