import createStore from "unistore";
// import axios from "axios";

const initialState = {
  npwp: "",
  pin: "",
  role: "",
  officer: false
};

export const store = createStore(initialState);

export const actions = store => ({
  // Fungsi untuk mengubah state sesuai dengan inputan pada kotak input
  handleInputLogin: (state, event) => {
    store.setState({ [event.target.name]: event.target.value });
    console.log(`${event.target.name} :`, event.target.value);
  },

  handleGantiRole: async (state, event) => {
    await store.setState({ [event.target.name]: event.target.value });
    if (state.role === "Payer") {
      await store.setState({ officer: false });
    } else {
      await store.setState({ officer: true });
    }
    console.log("officer", store.getState().officer);
    console.log(`${event.target.name} :`, event.target.value);
  }
});
